void decimal_add_matrix(double a[matrix_size][matrix_size], double b[matrix_size][matrix_size], double c[matrix_size][matrix_size]){
    int i = 0, j = 0;
    for(i = 0; i<matrix_size; i++){
        for(j = 0; j < matrix_size; j++){
            c[i][j] = a[i][j] + b[i][j];
        }
    }
}

void decimal_subtract_matrix(double a[matrix_size][matrix_size], double b[matrix_size][matrix_size], double c[matrix_size][matrix_size]){
    int i = 0, j = 0;
    for(i = 0; i<matrix_size; i++){
        for(j = 0; j < matrix_size; j++){
            c[i][j] = a[i][j] - b[i][j];
        }
    }
}

void decimal_multiply_matrix(double a[matrix_size][matrix_size], double b[matrix_size][matrix_size], double c[matrix_size][matrix_size]){
    int i = 0, j = 0;
    for(i = 0; i<matrix_size; i++){
        for(j = 0; j < matrix_size; j++){
            c[i][j] = a[i][j] * b[i][j];
        }
    }
}

void decimal_divide_matrix(double a[matrix_size][matrix_size], double b[matrix_size][matrix_size], double c[matrix_size][matrix_size]){
    int i = 0, j = 0;
    for(i = 0; i<matrix_size; i++){
        for(j = 0; j < matrix_size; j++){
            c[i][j] = a[i][j] / b[i][j];
        }
    }
}
















